INSERT INTO vocabulaire_musical (nom, definition, image, audio) VALUES
('Latin', 'Langue indo-européenne de l\'Antiquité parlée à Rome et en Italie, à l\'origine des langues romanes modernes et utilisée longtemps comme langue savante en Europe.', NULL, NULL),
('Grec', 'Langue indo-européenne parlée en Grèce antique, connue pour ses grands textes littéraires, philosophiques et scientifiques en grec ancien, ancêtre du grec moderne.', NULL, NULL),
('Langues créoles', 'Langues nées du contact entre plusieurs langues, devenues langues maternelles, souvent issues de situations coloniales, comme le créole haïtien ou le créole réunionnais.', NULL, NULL),
('Langues pidgin', 'Langues simplifiées créées pour permettre la communication entre groupes ne partageant pas de langue commune, généralement utilisées comme langues secondaires.', NULL, NULL),
('Langues signées', 'Langues naturelles utilisant les gestes, expressions faciales et mouvements des mains pour communiquer, comme la langue des signes française (LSF) ou l\'American Sign Language (ASL).', NULL, NULL),
('Langues construites', 'Langues inventées volontairement par des individus ou des groupes pour des buts spécifiques (communication universelle, œuvres de fiction ou musicales), comme l\'espéranto, le Sindarin (langue des Elfes de la Terre du Milieu) ou le Kobaïen du groupe Magma.', NULL, NULL),
('Langues aborigènes', 'Ensemble de centaines de langues parlées traditionnellement par les peuples aborigènes d\'Australie, souvent très variées et appartenant à plusieurs familles distinctes comme le pama-nyungan.', NULL, NULL),
('Langues amérindiennes', 'Ensemble très diversifié de langues parlées par les peuples autochtones des Amériques, comme le quechua, le nahuatl, le guarani et le navajo.', NULL, NULL),
('Langue coréenne', 'Langue parlée en Corée du Sud et du Nord, utilisant l\'alphabet hangul, considérée comme une langue isolée sans parenté clairement établie avec d\'autres familles linguistiques.', NULL, NULL),
('Langue japonaise', 'Langue isolée parlée au Japon, utilisant trois systèmes d\'écriture (hiragana, katakana, kanji) et n\'ayant pas de lien direct confirmé avec d\'autres familles linguistiques.', NULL, NULL),
('Langues sino-tibétaines', 'Grande famille linguistique d\'Asie regroupant le chinois (mandarin, cantonais), le tibétain et le birman.', NULL, NULL),
('Langues dravidiennes', 'Famille de langues du sud de l\'Inde et du Sri Lanka, comprenant le tamoul, le télougou, le kannada et le malayalam.', NULL, NULL),
('Langues ouraliennes', 'Famille de langues d\'Europe du Nord et de Sibérie, dont le finnois, l\'estonien et le hongrois sont les principales représentantes.', NULL, NULL),
('Langues altaïques', 'Groupe hypothétique rassemblant les langues turques, mongoles et toungouses d\'Asie centrale et orientale.', NULL, NULL),
('Langues austroasiatiques', 'Famille de langues d\'Asie du Sud-Est comprenant le vietnamien, le khmer (cambodgien) et plusieurs langues tribales.', NULL, NULL),
('Langues austronésiennes', 'Grande famille de langues parlées des îles du Pacifique jusqu\'à Madagascar, incluant le malais, le tagalog et le maori.', NULL, NULL),
('Langues afro-asiatiques', 'Famille de langues parlées principalement en Afrique du Nord et au Moyen-Orient, incluant l\'arabe, le berbère, et le haoussa.', NULL, NULL),
('Langues nilo-sahariennes', 'Groupe hypothétique de langues parlées autour du Nil et du Sahara central, comprenant le dinka, le kanuri et d\'autres langues minoritaires.', NULL, NULL),
('Langues nigéro-congolaises', 'La plus grande famille de langues africaines en nombre de langues, comprenant le swahili, le yoruba et le zoulou.', NULL, NULL),
('Langues khoïsan', 'Groupe de langues d\'Afrique australe caractérisées par l\'utilisation de clics, parlées par les peuples khoïkhoïs et sans.', NULL, NULL),
('Langues oubanguiennes', 'Sous-groupe de langues nigéro-congolaises parlées principalement en République centrafricaine et ses alentours, incluant le sango.', NULL, NULL),
('Langues mandé', 'Sous-famille de langues nigéro-congolaises parlée en Afrique de l\'Ouest, incluant le bambara, le malinké et le soninké.', NULL, NULL),
('Langues germaniques', 'Groupe de langues indo-européennes apparu en Europe du Nord, comprenant l\'anglais, l\'allemand, le néerlandais et les langues scandinaves.', NULL, NULL),
('Langues romanes', 'Groupe de langues issues du latin vulgaire de l\'Empire romain, incluant le français, l\'espagnol, l\'italien, le portugais et le roumain.', NULL, NULL),
('Langues slaves', 'Famille de langues indo-européennes parlées en Europe de l\'Est et du Nord, comme le russe, le polonais, le tchèque et le serbe.', NULL, NULL),
('Langues finno-ougriennes', 'Groupe de langues non indo-européennes d\'Europe du Nord et de l\'Oural, comprenant le finnois, l\'estonien et le hongrois.', NULL, NULL),
('Langues celtiques', 'Anciennes langues indo-européennes parlées historiquement en Europe de l\'Ouest, dont le breton, le gaélique irlandais et le gallois.', NULL, NULL),
('Langue basque', 'Langue isolée, sans lien connu avec d\'autres familles, parlée au Pays basque entre la France et l\'Espagne.', NULL, NULL),
('Musique occidentale de tradition écrite', 'Style musical savant européen caractérisé par son système de notation et son évolution historique à travers les périodes stylistiques majeures.', NULL, NULL),
('Musique du Moyen-Âge', 'Style (Ve-XVe siècles) marqué par le chant grégorien, l\'organum, les troubadours et l\'émergence de la polyphonie. Instruments typiques : vièle, harpe, flûte à bec.', NULL, NULL),
('Musique de la Renaissance', 'Style (XVe-XVIe siècles) caractérisé par la polyphonie vocale complexe et le développement des instruments comme le luth et le clavecin.', NULL, NULL),
('Style baroque', 'Style (1600-1750) marqué par la basse continue, l\'opéra, les formes concertantes et les grands maîtres comme Bach et Haendel.', NULL, NULL),
('Style classique', 'Style (1750-1800) caractérisé par la clarté formelle, l\'équilibre et les compositeurs comme Mozart et Haydn.', NULL, NULL),
('Style romantique', 'Style (1800-1900) privilégiant l\'expression des émotions, les formes libres et les grands virtuoses comme Chopin et Liszt.', NULL, NULL),
('Musique moderne', 'Style (début XXe siècle) marqué par la rupture avec le système tonal et l\'émergence de nouvelles techniques compositionnelles.', NULL, NULL),
('Musique contemporaine', 'Style actuel (après 1945) explorant des approches innovantes comme la musique concrète ou minimaliste.', NULL, NULL),
('Musiques traditionnelles / folk', 'Style musical transmis oralement, propre à une culture ou région, utilisant souvent des instruments locaux caractéristiques.', NULL, NULL),
('Musique celtique', 'Style musical des pays celtes utilisant harpe, cornemuse et violon, avec des mélodies caractéristiques.', NULL, NULL),
('Flamenco', 'Style andalou associant chant passionné, danse expressive et guitare rythmique.', NULL, NULL),
('Fado', 'Style chanté portugais exprimant la nostalgie, accompagné de la guitare portugaise.', NULL, NULL),
('Gnawa', 'Style marocain aux rythmes hypnotiques, mêlant traditions africaines et soufies.', NULL, NULL),
('Blues traditionnel', 'Style afro-américain né dans le Delta du Mississippi, basé sur des structures simples et une expressivité vocale intense.', NULL, NULL),
('Musique africaine', 'Style diversifié caractérisé par des polyphonies et des polyrythmies complexes.', NULL, NULL),
('Musiques populaires modernes', 'Styles musicaux apparus principalement au XXe siècle, caractérisés par :\n1. Une large diffusion médiatique (radio, disques, streaming)\n2. Une production souvent industrielle\n3. Une forte association avec la culture de masse\n4. L\'utilisation d\'instruments amplifiés/électroniques\n\nPrincipales caractéristiques :\n- Structures souvent simples et répétitives\n- Importance du rythme et de la mélodie\n- Forte connexion avec les mouvements sociaux et générationnels\n- Évolution rapide influencée par la technologie.', NULL, NULL),
('Jazz', 'Style musical né aux États-Unis combinant improvisation, syncopes et harmonies sophistiquées.', NULL, NULL),
('Rock', 'Style apparu dans les années 1950 centré sur les guitares électriques et une énergie rythmique puissante.', NULL, NULL),
('Reggae', 'Style jamaïcain au tempo modéré, marqué par l\'accentuation du contretemps et des textes engagés.', NULL, NULL),
('Rap', 'Style vocal parlé-rythmé, utilisant à l\'origine des samples en boucle comme accompagnement, élément central de la culture hip-hop.', NULL, NULL),
('Musiques du monde', 'Catégorie regroupant les traditions musicales non-occidentales et les fusions interculturelles, caractérisées par : une transmission souvent orale ou maître-élève, l\'utilisation d\'instruments traditionnels spécifiques, des systèmes musicaux différents de la théorie occidentale (échelles, rythmes), des fonctions sociales/cérémonielles importantes.', NULL, NULL),
('Samba', 'Style brésilien énergique associé au carnaval, basé sur des percussions complexes.', NULL, NULL),
('Raga', 'Style mélodique de la musique indienne lié à des moments précis et des émotions spécifiques.', NULL, NULL),
('Musique arabo-andalouse', 'Style résultant d\'un métissage entre musique arabe venue de l\'Orient, musique afro-berbère du Maghreb et musique pratiquée dans la Péninsule Ibérique avant le VIIIe siècle, utilisant des modes spécifiques.', NULL, NULL),
('Chants pygmées', 'Style vocal africain caractérisé par des polyphonies complexes et l\'utilisation du yodel.', NULL, NULL),
('Musiques rituelles ou spirituelles', 'Pratiques musicales sacrées ou cérémonielles remplissant une fonction médiatrice entre le profane et le sacré. Elles ont pour vocation d\'établir une communication avec les entités spirituelles (invocations, prières chantées), de faciliter les transitions ritualisées (rites de passage, cycles calendaires), de transmettre des connaissances religieuses par la mise en musique des textes sacrés, d\'induire des états de conscience modifiés (transe, extase, méditation).', NULL, NULL),
('Chamanisme', 'Style musical utilisé dans les pratiques spirituelles pour induire des états de transe.', NULL, NULL),
('Gospel', 'Style religieux afro-américain marqué par son intensité émotionnelle et ses harmonies vocales riches.', NULL, NULL),
('Qawwali', 'Style soufi visant à provoquer l\'extase mystique par la répétition rythmique et mélodique.', NULL, NULL),
('Musique de tradition orale', 'Style transmis sans support écrit, par imitation et mémoire collective.', NULL, NULL),
('Musique de tradition écrite', 'Pratique musicale fondée sur un système de notation précis permettant la fixation et la transmission des œuvres.', NULL, NULL),
('Musique savante', 'Style composé et noté, relevant d\'une démarche artistique élaborée.', NULL, NULL),
('Musique populaire', 'Musique transmise principalement par voie orale au sein d\'une communauté, associée à des pratiques collectives et quotidiennes, se distinguant de la musique savante par son mode de transmission et son ancrage dans une culture.', NULL, NULL),
('Cas hybrides', 'Pratiques musicales combinant des éléments de transmission orale et écrite, créant un continuum entre culture savante et populaire. Ces formes émergent lorsque des traditions orales sont notées, lorsque des musiques écrites intègrent des pratiques improvisées, ou lorsque des musiciens formés à l\'écrit revisitent des répertoires traditionnels. Exemples typiques : le jazz (improvisation orale sur structures harmoniques écrites), le flamenco (phrases mélodiques transmises oralement mais styles codifiés par écrit), ou certaines musiques baroques (partitions écrites avec ornementation improvisée).', NULL, NULL),
('Violon', 'Instrument à cordes frottées, le plus petit et aigu de sa famille. Joué avec un archet ou en pizzicato.', 'images/violon.jpg', 'audio/violon.mp3'),
('Violon alto', 'Instrument à cordes frottées, légèrement plus grand que le violon, avec un son plus chaud et grave.', 'images/alto.jpg', 'audio/alto.mp3'),
('Violoncelle', 'Instrument à cordes frottées joué assis, tenu entre les jambes. Tessiture entre l\'alto et la contrebasse.', 'images/violoncelle.jpg', 'audio/violoncelle.mp3'),
('Contrebasse', 'Le plus grand et le plus grave des instruments à cordes frottées, utilisé en classique et en jazz.', 'images/contrebasse.jpg', 'audio/contrebasse.mp3'),
