('baryton', 'Registre moyen des voix masculines.', 'images/baryton.jpg', 'audio/baryton.mp3'),
('basse', 'Registre le plus grave des voix masculines.', 'images/basse.jpg', 'audio/basse.mp3'),
('basse profonde', 'Basse avec une tessiture très grave, capable de produire des sons très bas.', NULL, 'audio/basse-profonde.mp3'),
('aigu', 'Un registre vocal élevé, souvent utilisé pour désigner des sons de haute fréquence, comme ceux produits par un soprano.', NULL, NULL),
('médium', 'Le registre vocal intermédiaire, ni trop aigu, ni trop grave. Il est souvent utilisé par des voix comme les mezzo-sopranos.', NULL, NULL),
('grave', 'Un registre vocal grave, souvent utilisé par des voix de basse ou des voix masculines plus profondes.', NULL, NULL),
