('antienne', 'Court chant liturgique, souvent répété avant et après un psaume.', NULL, NULL),
('cyclique', 'Forme où des thèmes ou motifs réapparaissent tout au long d\'une œuvre, créant une unité.', NULL, NULL),
('da capo', 'Indication pour répéter une section depuis le début (littéralement "depuis le début").', NULL, NULL),
('fugue', 'Forme contrapuntique où un thème (sujet) est introduit puis repris par différentes voix ou instruments. Typique de la musique baroque.', NULL, NULL),
('ground / chaconne / passacaille', 'Formes basées sur une basse répétée.', NULL, NULL),
('marche', 'Composition au rythme régulier, souvent utilisée pour accompagner des défilés.', NULL, NULL),
('petit rondo (ABA C ABA)', 'Variante du rondo.', NULL, NULL),
('reprise', 'Retour d’une section musicale après une autre partie.', NULL, NULL),
